import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Vimeo from '@u-wave/react-vimeo';

import { Container, Row, Col, Spinner } from 'reactstrap';

import { getVimeoShowcase } from 'actions/apiActions';

const VimeoShowcaseDemo = () => {
  const [activeIndex, setActiveIndex] = useState(1);

  const dispatch = useDispatch();

  const vimeoShowcaseObject = useSelector((state) => state.vimeoShowcaseObject);
  const { showcase, loading, error } = vimeoShowcaseObject;

  useEffect(() => {
    if (!showcase) {
      dispatch(getVimeoShowcase());
    }
  }, [showcase, dispatch]);

  return (
    <div className='section' id='carousel'>
      <Container>
        <div className='title'>
          <h4>Vimeo Showcase</h4>
        </div>
        <Row className='justify-content-center'>
          <Col lg='8' md='12'>
            {loading ? (
              <Spinner size='lg' color='info' />
            ) : (
              <Row>
                <Col sm='2'>
                  <i
                    className='tim-icons icon-minimal-left'
                    style={{
                      color: 'lightgray',
                      fontSize: '4rem',
                      cursor: 'pointer'
                    }}
                    onClick={() => carouselHandler('prev')}
                  />
                </Col>
                <Col sm='10'>
                  <Vimeo video={video.uri.slice(8)} autoplay />
                </Col>
                <Col sm='2'>
                  <i
                    className='tim-icons icon-minimal-right'
                    style={{
                      color: 'lightgray',
                      fontSize: '4rem',
                      cursor: 'pointer'
                    }}
                    onClick={() => carouselHandler('next')}
                  />
                </Col>
              </Row>
            )}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default VimeoShowcaseDemo;
