import React, { useState } from 'react';
import {
  Container,
  Row,
  Col,
  Button,
  Card,
  CardBody,
  CardTitle
} from 'reactstrap';
import './apiDemos.css';

const ApiHeader = () => {
  const [card1Classname, setCard1Classname] = useState('card-pricing');
  const [card2Classname, setCard2Classname] = useState(
    'card-pricing card-plain'
  );
  const [card3Classname, setCard3Classname] = useState(
    'card-pricing card-plain'
  );
  const [card4Classname, setCard4Classname] = useState(
    'card-pricing card-plain'
  );

  const updateClassname = (card) => {
    if (card === 1) {
      setCard1Classname('card-pricing');
      setCard2Classname('card-pricing card-plain');
      setCard3Classname('card-pricing card-plain');
      setCard4Classname('card-pricing card-plain');
    }
    if (card === 2) {
      setCard1Classname('card-pricing card-plain');
      setCard2Classname('card-pricing');
      setCard3Classname('card-pricing card-plain');
      setCard4Classname('card-pricing card-plain');
    }
    if (card === 3) {
      setCard1Classname('card-pricing card-plain');
      setCard2Classname('card-pricing card-plain');
      setCard3Classname('card-pricing');
      setCard4Classname('card-pricing card-plain');
    }
    if (card === 4) {
      setCard1Classname('card-pricing card-plain');
      setCard2Classname('card-pricing card-plain');
      setCard3Classname('card-pricing card-plain');
      setCard4Classname('card-pricing');
    }
  };
  return (
    <div className='pricing-1 section-image' id='pricing-1'>
      <Container>
        <Row>
          <Col className='ml-auto mr-auto text-center' md='6'>
            <h2 className='title'>
              Check out some of the API's I've worked with
            </h2>
            <h4 className='description'>
              You'll find demos of each of these API's on my page. These aren't
              the only one's I know and I can always learn the API's that will
              benefit you the most in no time!
            </h4>
            <div className='section-space'></div>
          </Col>
        </Row>
        <Row>
          <Col md='3'>
            <Card
              onMouseEnter={() => updateClassname(1)}
              className={card1Classname}
            >
              <CardBody>
                <h5 className='category'>Twilio</h5>
                <div className='icon icon-warning'>
                  <i className='now-ui-icons media-1_button-power'></i>
                </div>
                <CardTitle tag='h3'>$10</CardTitle>
                <ul>
                  <li>Complete documentation</li>
                  <li>Working materials in Sketch</li>
                </ul>
                <Button
                  className='btn-neutral btn-round'
                  color='warning'
                  href='#pablo'
                >
                  Add to Cart
                </Button>
              </CardBody>
            </Card>
          </Col>
          <Col md='3'>
            <Card
              onMouseEnter={() => updateClassname(2)}
              className={card2Classname}
            >
              <CardBody>
                <h5 className='category'>Alpha Pack</h5>
                <div className='icon icon-info'>
                  <i className='now-ui-icons objects_diamond'></i>
                </div>
                <CardTitle tag='h3'>$67</CardTitle>
                <ul>
                  <li>Working materials in EPS</li>
                  <li>6 months access to the library</li>
                </ul>
                <Button className='btn-round' color='info' href='#pablo'>
                  Add to Cart
                </Button>
              </CardBody>
            </Card>
          </Col>
          <Col md='3'>
            <Card
              onMouseEnter={() => updateClassname(3)}
              className={card3Classname}
            >
              <CardBody>
                <h5 className='category'>Charlie Pack</h5>
                <div className='icon icon-success'>
                  <i className='now-ui-icons media-2_sound-wave'></i>
                </div>
                <CardTitle tag='h3'>$69</CardTitle>
                <ul>
                  <li>Working materials in PSD</li>
                  <li>1 year access to the library</li>
                </ul>
                <Button
                  className='btn-neutral btn-round'
                  color='success'
                  href='#pablo'
                >
                  Add to Cart
                </Button>
              </CardBody>
            </Card>
          </Col>
          <Col md='3'>
            <Card
              onMouseEnter={() => updateClassname(4)}
              className={card4Classname}
            >
              <CardBody>
                <h5 className='category'>Extra Pack</h5>
                <div className='icon icon-danger'>
                  <i className='now-ui-icons education_atom'></i>
                </div>
                <CardTitle tag='h3'>$159</CardTitle>
                <ul>
                  <li>Complete documentation</li>
                  <li>2GB cloud storage</li>
                </ul>
                <Button
                  className='btn-neutral btn-round'
                  color='danger'
                  href='#pablo'
                >
                  Add to Cart
                </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ApiHeader;
