import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import Vimeo from '@u-wave/react-vimeo';

import {
  Container,
  Row,
  Col,
  Carousel,
  CarouselItem,
  CarouselIndicators,
  Spinner
} from 'reactstrap';

import { getVimeoShowcase } from 'actions/apiActions';

const VimeoShowcaseDemo = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [animating, setAnimating] = useState(false);
  const [carouselData, setCarouselData] = useState([
    {
      src: require('assets/img/bg1.jpg').default,
      altText: '',
      caption: 'Nature, United States'
    },
    {
      src: require('assets/img/bg3.jpg').default,
      altText: '',
      caption: 'Somewhere Beyond, United States'
    },
    {
      src: require('assets/img/bg4.jpg').default,
      altText: '',
      caption: 'Yellowstone National Park, United States'
    }
  ]);

  const dispatch = useDispatch();

  const vimeoShowcaseObject = useSelector((state) => state.vimeoShowcaseObject);
  const { showcase, loading, error } = vimeoShowcaseObject;

  useEffect(() => {
    if (!showcase) {
      dispatch(getVimeoShowcase());
    } else {
      // eslint-disable-next-line
      showcase.data.map((v) => {
        setCarouselData((carouselData) => [
          ...carouselData,
          {
            src: require(`${v.link}`).default,
            altText: '',
            caption: v.description
          }
        ]);
      });
    }
  }, [showcase, dispatch]);

  const onExiting = () => {
    setAnimating(true);
  };
  const onExited = () => {
    setAnimating(false);
  };
  const next = () => {
    if (animating) return;
    const nextIndex =
      activeIndex === showcase?.data?.length - 1 ? 0 : activeIndex + 1;
    setActiveIndex(nextIndex);
  };
  const previous = () => {
    if (animating) return;
    const nextIndex =
      activeIndex === 0 ? showcase?.data?.length - 1 : activeIndex - 1;
    setActiveIndex(nextIndex);
  };
  const goToIndex = (newIndex) => {
    if (animating) return;
    setActiveIndex(newIndex);
  };
  return (
    <div className='section' id='carousel'>
      <Container>
        <div className='title'>
          <h4>Vimeo Showcase</h4>
        </div>
        <Row className='justify-content-center'>
          <Col lg='8' md='12'>
            {loading ? (
              <Spinner size='lg' color='info' />
            ) : (
              <Carousel
                activeIndex={activeIndex}
                next={next}
                previous={previous}
              >
                <CarouselIndicators
                  items={showcase?.data}
                  activeIndex={activeIndex}
                  onClickHandler={goToIndex}
                />
                {carouselData.length > 1 &&
                  carouselData.map((video) => {
                    return (
                      <CarouselItem
                        onExiting={onExiting}
                        onExited={onExited}
                        key={video.uri}
                      >
                        <Vimeo video={video.uri.slice(8)} autoplay />
                        <div className='carousel-caption d-none d-md-block'>
                          <h5>{video.description}</h5>
                        </div>
                      </CarouselItem>
                    );
                  })}
                <a
                  className='carousel-control-prev'
                  data-slide='prev'
                  href='#pablo'
                  onClick={(e) => {
                    e.preventDefault();
                    previous();
                  }}
                  role='button'
                >
                  <i className='now-ui-icons arrows-1_minimal-left'></i>
                </a>
                <a
                  className='carousel-control-next'
                  data-slide='next'
                  href='#pablo'
                  onClick={(e) => {
                    e.preventDefault();
                    next();
                  }}
                  role='button'
                >
                  <i className='now-ui-icons arrows-1_minimal-right'></i>
                </a>
              </Carousel>
            )}
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default VimeoShowcaseDemo;
